export const newBlog = (blog) => {
    return async dispatch => {
        dispatch({
            type: 'newBlog',
            blog
        })
    }
}

const blogReducer = (state = [], action) => {
    switch (action.type) {
        case 'newBlog':
            return [...state, action.blog]
        default: return state
    }
}

export default blogReducer
