import React, { useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import NewBlog from './components/NewBlog'
import Notification from './components/Notification'
import Togglable from './components/Togglable'
import { useField } from './hooks'
import { newNotification } from './reducers/notificationReducer'
import { addBlog, initializeBlogs, likeBlog, deleteBlog } from './reducers/blogReducer'
import { logIn, logOut } from './reducers/userReducer'
import { initializeUsers } from './reducers/usersReducer'
import { connect } from 'react-redux'
import {
  BrowserRouter as Router,
  Route, Link
} from 'react-router-dom'

const App = (props) => {
  const [username] = useField('text')
  const [password] = useField('password')

  useEffect(() => {
    const initializeBlogs = props.initializeBlogs
    initializeBlogs()
  }, [props.initializeBlogs])

  useEffect(() => {
    const initializeUsers = props.initializeUsers
    initializeUsers()
  }, [props.initializeUsers])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogAppUser')
    const logIn = props.logIn
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      logIn(user)
      blogService.setToken(user.token)
    }
  }, [props.logIn])

  const notify = (message, notificationType, time) => {
    props.newNotification(message, notificationType, time)
  }

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username: username.value,
        password: password.value
      })

      window.localStorage.setItem('loggedBlogAppUser', JSON.stringify(user))
      blogService.setToken(user.token)
      props.logIn(user)
    } catch (exception) {
      notify('wrong username of password', 'error', 5000)
    }
  }

  const handleLogout = () => {
    props.logOut()
    blogService.destroyToken()
    window.localStorage.removeItem('loggedBlogAppUser')
  }

  const createBlog = async (blog) => {
    props.addBlog(blog)
    notify(`a new blog ${blog.title} by ${blog.author} added`, 'success', 5000)
  }

  const likeBlog = async (blog) => {
    props.likeBlog(blog)
    notify(`blog ${blog.title} by ${blog.author} liked!`, 'success', 5000)
  }

  const removeBlog = async (blog) => {
    const ok = window.confirm(`remove blog ${blog.title} by ${blog.author}`)
    if (ok) {
      props.deleteBlog(blog)
      notify(`blog ${blog.title} by ${blog.author} removed!`, 'success', 5000)
    }
  }

  const BlogsListing = (props) => {
    const style = {
      border: '1px black solid',
      textDecorationLine: 'underline'
    }
    return (
      <div>
        {props.blogs.sort(byLikes).map(blog =>
  <Link to={'/blogs/' + blog.id} key={blog.id}><p style={style}>{blog.title + ' ' + blog.author}</p></Link>

        )}
      </div>
    )
  }

  const UsersInfo = (props) => {
    const users = props.users
    console.log(users)
    return (
      <div>
        <h2>Users</h2>
        <table>
          <thead>
            <tr>
              <th></th>
              <th>blogs created</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => <tr key={user.id}><td><Link to={'/users/' + user.id}>{user.name}</Link></td><td>{user.blogs.length}</td></tr>)}
          </tbody>
        </table>
      </div>
    )
  }

  const User = ({user}) => {
    console.log(user)
    if (user === undefined) {
      return null
    }
    return (
      <div>
        <h2>{user.name}</h2>
        <h3>added blogs</h3>
        <ul>
          {user.blogs.map(blog => <li key={blog.id}>{blog.title}</li>)}
        </ul>
      </div>
    )
  }

  if (props.user === null) {
    return (
      <div>
        <h2>log in to application</h2>

        <Notification />

        <form onSubmit={handleLogin}>
          <div>
            käyttäjätunnus
            <input {...username} />
          </div>
          <div>
            salasana
            <input {...password} />
          </div>
          <button type="submit">kirjaudu</button>
        </form>
      </div>
    )
  }

  const newBlogRef = React.createRef()

  const byLikes = (b1, b2) => b2.likes - b1.likes

  const userById = (id) => {
    return props.users.find(user => user.id === id)
  }

  const blogById = (id) => {
    return props.blogs.find(blog => blog.id === id)
  }
  return (
    <div>
      <Router>
        <h2>blogs</h2>
        <Notification />
        <p>{props.user.name} logged in</p>
        <button onClick={handleLogout}>logout</button>
        <Route exact path='/' render={() =>
          <div>
            <Togglable buttonLabel='create new' ref={newBlogRef}>
              <NewBlog createBlog={createBlog} />
            </Togglable>
            <BlogsListing blogs={props.blogs} user={props.user} /></div>} />
        <Route exact path='/users' render={() => <UsersInfo users={props.users} />} />
        <Route exact path="/users/:id" render={({ match }) =>
          <User user={userById(match.params.id)} />
        } />
        <Route exact path="/blogs/:id" render={({ match }) =>
          <Blog blog={blogById(match.params.id)}
          like={likeBlog}
          remove={removeBlog}
          user={props.user} />
        } />
      </Router>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    blogs: state.blogs,
    user: state.user,
    users: state.users
  }
}

const mapDispatchToProps = {
  newNotification,
  addBlog,
  initializeBlogs,
  likeBlog,
  deleteBlog,
  logIn,
  logOut,
  initializeUsers
}

const connectedApp = connect(
  mapStateToProps,
  mapDispatchToProps
)(App)

export default connectedApp
