import userService from '../services/users'

export const initializeUsers = () => {
    return async dispatch => {
        const users = await userService.getAll()
        dispatch({
            type: 'INIT_USERS',
            users
        })
    }
}

const usersReducer = (state = [], action) => {
    switch (action.type) {
        case 'INIT_USERS':
            return action.users
        default:
            return state
    }
}

export default usersReducer
